<?php#############################################################################  Calculate the distance between two pairs of earth coordinates############################################################################function degreesToRadians($value) {	$radians=$value/57.29577951;	return $radians;}function calcDist($a, $b, $c, $d) {	#convert values from degrees to radians	$a=degressToRadians($a);	$b=degressToRadians($b);	$c=degressToRadians($c);	$d=degressToRadians($d);	# here comes the actual formula that does the calculation in radians and then  	# converts to miles	if( $a == $c && $b == $d ) {		$distance = 0; 	} else if ( (sin($a) * sin($c) + cos($a) * cos($c) * cos($b-$d)) > 1 ) {		$distance = 3963.1*acos(1); 	    /* this solved a prob that the guy who derived the formula ran into.  He hadn't fully 	   analyzed it by the time I wrote this script.  I got the formula from 	   http://www.mathforum.com/library/drmath/view/51711.html     	   EG	   */	} else {		$distance=3963.1 * acos(sin($a) * sin($c) + cos($a) * cos($c) * cos($b-$d));	}	 return round($distance); }?>